import React from 'react';
import {
    View,
    Text,
    TouchableOpacity,
    Image,
    ScrollView,
    Platform
} from 'react-native';
import ToggleSwitch from 'toggle-switch-react-native'
import { Drawer, Container, Content } from 'native-base'
import {  Actions } from 'react-native-router-flux';
import {  connect } from 'react-redux';
import { bindActionCreators } from 'redux';
import { ThemeSettings, ImageSources, ScreenNames } from '../../../constants';
import { DrawerMenuItem } from '../../../components/legacy/DrawerMenuItem'
import { StandardHeader } from '../../../components/legacy/Header';
import styles from './style'
import { AsyncStorage } from 'react-native';


class SettingsView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isOnDefaultToggleSwitch: false,
        };
    }

    _onLogout = () => {
        AsyncStorage.removeItem('userData');
        Actions.reset(ScreenNames.root);
    }

    render() {
        return (
            <Container>
                <StandardHeader drawer title={"Settings"} />
                <Content>
                    <View>
                        <View style={{ flexDirection: 'row', justifyContent: 'center', marginLeft: 10 }}>
                            <View style={{ flex: 1.5, justifyContent: 'center', marginBottom: 10 }}>
                                <Image style={{ width: Platform.OS === 'ios' ? 40 : 30, height: Platform.OS === 'ios' ? 40 : 30, borderRadius: Platform.OS === 'ios' ? 20 : 60, }} source={ImageSources.DemoProfilePic} />
                            </View>
                            <View style={{ flex: 8.5, justifyContent: 'center', marginBottom: 10, paddingLeft: 6 }}>
                                <Text style={{ fontSize: 15, fontFamily: ThemeSettings.inputFont }}>Annie R. Dodd</Text>
                            </View>
                        </View>

                        <View style={{ marginLeft: 5 }}>
                            <DrawerMenuItem icon='envelope' title='annieRDodd221b@gmail.com' />
                            <DrawerMenuItem icon='phone' title='+91 1234567890' />
                            {/* <DrawerMenuItem icon='building' title='Business profile' />
                            <Text style={{ fontSize: 10, color: ThemeSettings.inputPlaceholderColor, marginLeft: 62, bottom: 12 }}>Create a Business profile</Text> */}
                        </View>



                        <View style={{ borderTopWidth: 0.5, borderTopColor: ThemeSettings.buttonBorderColor, marginTop: 10 }}></View>

                        <View style={{ marginLeft: 5, marginTop: 6 }}>
                            <DrawerMenuItem icon='sign-out' title='Log Out' onPress={this._onLogout} />
                        </View>

                    </View>
                </Content>
            </Container>
        );
    }
}



export default SettingsView;
