  'back',
  'children',
  'create',
  'dispatch',
  'drawerClose',
  'drawerOpen',
  'execute',
  'left',
  'leftButton',
  'navBar',
  'navigate',
  'on',
  'onEnter',
  'onExit',
  'onLeft',
  'onRight',
  'pop',
  'popTo',
  'push',
  'refresh',
  'refs',
