    backToInitial,
    component,
    drawerIcon,
    drawerImage,
    drawerPosition,
    getTitle,
    headerLayoutPreset,
    headerStyle,
    headerTitleStyle,
    hideDrawerButton,
    hideNavBar,
    hideTabBar,
